<?phpnamespace App\Http\Controllers\API\Admin;use App\Helpers\Core\Multitenant;use App\Http\Controllers\Controller;use App\Http\Resources\CategoryResource;use App\Http\Resources\GalleryResource;use App\Http\Resources\MenuResource;use App\Http\Resources\PageResource;use App\Http\Resources\PostResource;use App\Http\Resources\ProductResource;use App\Http\Resources\ServiceResource;use App\Http\Resources\User;use App\Http\Resources\UsersResource;use App\Models\Core\Category;use App\Models\Core\Page;use Illuminate\Http\Request;use Illuminate\Support\Arr;class OptionsController extends Controller{    /**     * @return PageResource     */    public function getPageConfig()    {        $data['statuses']   = config('page.status');        $data['types']      = config('page.types');        $data['templates']  = array_values(config('page.templates'));        $data['ogTypes']    = config('social.ogTypes');        $data['coverTypes'] =  array_values(Arr::only(config('files.covers'), config('page.covers')));        return new PageResource(collect($data));    }    /**     * @return PostResource     */    public function getPostConfig()    {        $data['statuses']   = config('page.status');        $data['types']      = config('page.types');//        $data['templates']  = array_values(config('page.templates'));        $data['ogTypes']    = config('social.ogTypes');        $data['coverTypes'] =  array_values(Arr::only(config('files.covers'), config('page.covers')));        $data['categories'] = Category::whereHas('info', function($query) {            $query->where('language_id',  1);        })->with('info')->get();        return new PostResource(collect($data));    }    /**     * @return ServiceResource     */    public function getServiceConfig()    {        $data['statuses']   = config('page.status');        $data['types']      = config('page.types');//        $data['templates']  = array_values(config('page.templates'));        $data['ogTypes']    = config('social.ogTypes');        $data['coverTypes'] =  array_values(Arr::only(config('files.covers'), config('page.covers')));        $data['categories'] = Category::whereHas('info', function($query) {            $query->where('language_id',  1);        })->with('info')->get();        return new ServiceResource(collect($data));    }    /**     * @return CategoryResource     */    public function getCategoryConfig()    {        $data['statuses']   = config('page.status');        $data['coverTypes'] =  array_values(Arr::only(config('files.covers'), config('page.covers')));        $data['pages'] = Page::query()->whereIn('template_id',[            config('page.templates.services.id'),            config('page.templates.products.id'),            config('page.templates.calculate.id'),            config('page.templates.standard_picture.id')        ])->with('info')->get();        return new CategoryResource(collect($data));    }    /**     * @return ProductResource     */    public function getProductConfig()    {        $data['statuses']   = config('page.status');        $data['coverTypes'] =  array_values(Arr::only(config('files.covers'), config('page.covers')));        $data['categories'] =  Category::whereHas('info', function($query) {            $query->where('language_id',  1);        })->with('info')->get();        return new ProductResource(collect($data));    }    /**     * @return CategoryResource     */    public function getSliderConfig()    {        $data['statuses']   = config('page.status');        $data['coverTypes'] =  array_values(Arr::only(config('files.covers'), config('slider.covers')));        return new CategoryResource(collect($data));    }    /**     * @return GalleryResource     */    public function getGalleryConfig()    {        $data['statuses']   = config('page.status');        $data['coverTypes'] =  array_values(Arr::only(config('files.covers'), config('gallery.covers')));        return new GalleryResource(collect($data));    }    /**     * @return GalleryResource     */    public function getTeamConfig()    {        $data['statuses']   = config('page.status');        $data['coverTypes'] =  array_values(Arr::only(config('files.covers'), config('team.covers')));        return new GalleryResource(collect($data));    }    /**     * @return MenuResource     */    public function getMenuConfig(){        $data['statuses'] = config('menu.status');        $data['positions'] =  array_values(config('menu.positions'));        $data['pages']= Page::where(['status'=>config('page.status.active')])->with('info')->get();        return new MenuResource(collect($data));    }    /**     * @return UsersResource     */    public function getUsersConfig(){        $data['statuses'] = config('page.status');        return new UsersResource(collect($data));    }    /**     * @return GalleryResource     */    public function getSettingsConfig(){        $data['statuses'] = config('page.status');        $data['coverTypes'] =  array_values(Arr::only(config('files.covers'), config('settings.covers')));        return new GalleryResource(collect($data));    }}